import React, { useEffect, useState } from 'react'
import './main.css'
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

function Main() {
    const navigate = useNavigate();
    const [progress, setProgress] = useState(0);
    const [reviewOrder, setReviewOrder] = useState(0);
    const [projectData, setProjectData] = useState([]);
    const location = useLocation();
    const leadRegNo = new URLSearchParams(location.search).get('leadRegNo');
    
    const handleLogout = (event) => {
        localStorage.setItem("isSigned", "false");
        localStorage.setItem("isauthorized", "");
        navigate("/");
      };

      useEffect(() => {
        // Fetch data from the backend using Axios
        const fetchData = async () => {
          try {
            const response = await axios.post('http://localhost:8000/allteams/student/', {
              leadRegNo: leadRegNo, // Pass any other data needed for the request
            });
            // Assuming the backend response is an array of project data
            setProjectData(response.data);
            console.log(response.data)
          } catch (error) {
            console.error('Error fetching data:', error);
          }
        };
    
        // Call the fetchData function
        fetchData();
      }, [leadRegNo]); 




      const handleRadioChange = (value) => {
        let newProgress = 0;
    
        switch (value) {
          case 'twentyfive':
            newProgress = 25;

            setReviewOrder(1);
            break;
          case 'fifty':
            if (reviewOrder === 1) {
              newProgress = 50;
              setReviewOrder(2);
            }
            break;
          case 'seventyfive':
            if (reviewOrder === 2) {
              newProgress = 75;
              setReviewOrder(3);
            }
            break;
          case 'onehundred':
            if (reviewOrder === 3) {
              newProgress = 100;
              setReviewOrder(4);
            }
            break;
          default:
            break;
        }
    
        setProgress(newProgress);

      const progressBar = document.querySelector('.progress');
      console.log(progressBar);  // Log the element to the console
      progressBar.className = `progress ${value}-checked`;

      };
  return (
    <div>
       <nav class="navbar navbar-expand-lg navbar-dark" id="navi">
        <div class="container-fluid">
          <div class="collapse navbar-collapse" id="navbarSupportedContent">
            <ul class="navbar-nav me-auto mb-2 mb-lg-0">
              <li class="nav-link">
                <label id='h4'>Student Profile</label>
              </li>
              <li>
                <a class="nav-link" href="#" id='h3'>Project Details</a>
            </li>
            </ul>
              <form class="d-flex">
                <button class="custom-btn btn-3" value="Log Out" onClick={handleLogout}><span>Log Out</span></button>
              </form>
          </div>
        </div>
      </nav>
      
      <div id='cen'>
        <h1><i><b>Title</b></i></h1>
      </div>
      <div className='row' id='cen2'>
        <div className='col'>
          <div className='row'>
            <div className='col d-flex flex-row-reverse'>
              <h5>Project Guide:</h5>
