import React, { useState } from 'react';
import axios from 'axios';

const NewGuideForm = ({ onClose }) => {
  const [name, setName] = useState('');
  const [regNo, setRegNo] = useState('');

  const handleAddGuide = () => {
    
    axios.post('http://localhost:8000/addGuide', { name, regNo })
      .then(response => {
        console.log('Guide added successfully:', response.data);
        onClose();
      })
      .catch(error => {
        console.error('Error adding guide:', error);
      });
  };

  return (
    <div className="new-guide-form">
      <label htmlFor="name">Name:</label>
      <input type="text" id="name" value={name} onChange={(e) => setName(e.target.value)} />

      <label htmlFor="regNo">Reg No:</label>
      <input type="text" id="regNo" value={regNo} onChange={(e) => setRegNo(e.target.value)} />

      <button onClick={handleAddGuide}>Add</button>
    </div>
  );
};

export default NewGuideForm;
