import React, { useState } from 'react';
import './dash.css';

function Dashboard() {
  const [fieldList, setFieldList] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const handleAddButtonClick = () => {
    if (title && description) {
      // Add the new title and description to the array
      setFieldList([...fieldList, { title, description }]);
      // Clear the input fields
      setTitle('');
      setDescription('');
    }
  };

  return (
    <div>
      <div className="container">
        <div className="row profile">
          <div className="col-md-3">
            <div className="profile-sidebar">
              <div className="profile-userpic">
                <img
                  src="https://gravatar.com/avatar/31b64e4876d603ce78e04102c67d6144?s=80&d=https://codepen.io/assets/avatars/user-avatar-80x80-bdcd44a3bfb9a5fd01eb8b86f9e033fa1a9897c3a15b33adfc2649a002dab1b6.png"
                  className="img-responsive"
                  alt=""
                />
              </div>

              <div className="profile-usertitle">
                <div className="profile-usertitle-name">Jason Davis</div>
                <div className="profile-usertitle-job">Developer</div>
              </div>
            </div>
          </div>
          <div className="col-md-9">
            <div className="profile-content">
              <div>
                <div className="mb-3">
                  <label htmlFor="exampleFormControlInput1" className="d-flex justify-content-start">
                    Title
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="exampleFormControlInput1"
                    placeholder="Title/Sub Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="exampleFormControlTextarea1" className="d-flex justify-content-start">
                    Description
                  </label>
                  <textarea
                    className="form-control"
                    id="exampleFormControlTextarea1"
                    rows="3"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                  ></textarea>
                </div>
                <button onClick={handleAddButtonClick} className="btn btn-primary">
                  Add
                </button>
              </div>

              {fieldList.map((field, index) => (
                <div key={index}>
                  <h3>{field.title}</h3>
                  <p>{field.description}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
