import React, { useState, useEffect } from 'react';
import './main.css';

function Main() {
  const [activeTab, setActiveTab] = useState('Dashboard');

  useEffect(() => {
    updateSelector();

    const handleWindowResize = () => {
      updateSelector();
    };

    // Run test on initial render
    setTimeout(() => {
      updateSelector();
    });

    // Run test on window resize
    window.addEventListener('resize', handleWindowResize);

    // Cleanup function
    return () => {
      window.removeEventListener('resize', handleWindowResize);
    };
  }, [activeTab]);

  const updateSelector = () => {
    const activeItem = document.querySelector(`.nav-item[data-tab="${activeTab}"]`);
    if (activeItem) {
      const rect = activeItem.getBoundingClientRect();
      const horiSelector = document.querySelector('.hori-selector');

      horiSelector.style.top = `${rect.top}px`;
      horiSelector.style.left = `${rect.left}px`;
      horiSelector.style.height = `${rect.height}px`;
      horiSelector.style.width = `${rect.width}px`;
    }
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div>
      <nav className="navbar navbar-expand-custom navbar-mainbg">
        <a className="navbar-brand navbar-logo" href="#">
          Navbar
        </a>
        <button
          className="navbar-toggler"
          type="button"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <i className="fas fa-bars text-white"></i>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav ml-auto">
            <div className="hori-selector">
              <div className="left"></div>
              <div className="right"></div>
            </div>
            <li className="nav-item" data-tab="Dashboard" onClick={() => handleTabClick('Dashboard')}>
              <a className={`nav-link ${activeTab === 'Dashboard' ? 'active' : ''}`}>
                <i className="fas fa-tachometer-alt"></i>Dashboard
              </a>
            </li>
            <li className="nav-item" data-tab="Address Book" onClick={() => handleTabClick('Address Book')}>
              <a className={`nav-link ${activeTab === 'Address Book' ? 'active' : ''}`}>
                <i className="far fa-address-book"></i>Address Book
              </a>
            </li>
            {/* Add other menu items as needed */}
          </ul>
        </div>
      </nav>
    </div>
  );
}

export default Main;
