import React, { useEffect } from 'react'
import { useNavigate } from "react-router-dom";
import Dashboard from './Dashboard';
import ProjectDetail from './ProjectDetail';
import './studen.css';

function Student() {
  const isauthorized = localStorage.getItem("isauthorized");
  const navigate = useNavigate();
  useEffect(() => {
    if (isauthorized !== "student") {
      navigate("/");
    }
  }, []);
  const handleLogout = (event) => {
    localStorage.setItem("isSigned", "false");
    localStorage.setItem("isauthorized", "");
    navigate("/");
  };
  return (
    <div>
      <div class="window-margin">
  <div class="window">

    <aside class="sidebar">
      <div class="top-bar">
        <p class="logo">REPLEX</p>
      </div>
    </aside>

    <div class="main" role="main">

      <div class="top-bar">

        <div class="profile-box">
          <div class="circle"></div>
          <span class="arrow fa fa-angle-down"></span>
        </div>

        <ul class="top-menu">
          <li class="menu-icon trigger-sidebar-toggle">
            <div class="line"></div>
            <div class="line"></div>
            <div class="line"></div>
          </li>
          <li><a href="#">Headlines</a></li>
          <li><a href="#">Articles</a></li>
          <li class="active"><a href="#">Movies & Films</a></li>
          <li><a href="#">Television</a></li>
          <li><a href="#">Music</a></li>
          <li><a href="#">Celebrities</a></li>
        </ul>
      </div></div>
  </div>
</div> 
      <button class="custom-btn btn-2" value="Log Out" onClick={handleLogout}>Log Out</button>
      <Dashboard/>
    </div>
  )
}

export default Student
