import React, { useEffect } from 'react'
import { Navigate, useNavigate } from "react-router-dom";

function Student() {
  const isauthorized = localStorage.getItem("isauthorized");
  useEffect(() => {
    // Check if the user is authorized to access this component
    if (isauthorized !== "student") {
      // Redirect to the appropriate route if not authorized
      Navigate("/"); // Replace '/' with the desired route for unauthorized access
    }
  }, []);
  const handleLogout = (event) => {
    localStorage.setItem("isSigned", "false");
    localStorage.setItem("isauthorized", "");
  };
  return (
    <div>
      <h1>Student Module</h1>
      <input type='button' value="Log In" onClick={handleLogout}/>
    </div>
  )
}

export default Student
